package curso.java.marcio;

public class Exercicio_Switch_Case {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        
	
		int dia = 8;
		switch (dia) {
		case 1:
			System.out.println("pagar contas");
			
			break;
		case 2:
			System.out.println("viajar para Salvador");
			break;
		case 3:
			System.out.println("Trabalhar fora");
				break;	
          
		default: System.out.println("outro dia");
			break;
		}
	}

}
